import React, {useState} from "react";
import {Linking, TouchableOpacity, View} from "react-native";
import { MainStackParamList } from "../types/navigation";
import { NativeStackScreenProps } from "@react-navigation/native-stack";
import {Button, Layout, Text, TextInput, themeColor, TopNav, useTheme} from "react-native-rapi-ui";
import {Ionicons} from "@expo/vector-icons";

export default function ({
  navigation,
}: NativeStackScreenProps<MainStackParamList, "MainTabs">) {
    const { isDarkmode, setTheme } = useTheme();
    const [inputs, setInputs ] = useState([{key: '', value: ''}])

    const addHandler = () => {
        const _inputs = [...inputs];
        _inputs.push({key: '', value: ''});
        setInputs(_inputs);
    }

    const deleteHandler = (key: number) => {
        const _inputs = inputs.filter((input, index) => index != key);
        setInputs(_inputs);
    }

    const inputHandler = (text: any, key: any) => {
        const _inputs = [...inputs];
        _inputs[key].value = text;
        _inputs[key].key = key;
        setInputs(_inputs);
    }

    return (
    <Layout>
        <TopNav
            middleContent="Choix des joueurs"
            leftContent={
                <Ionicons
                    name="chevron-back"
                    size={20}
                    color={isDarkmode ? themeColor.white100 : themeColor.dark}
                />
            }
            leftAction={() => navigation.goBack()}
            rightContent={
                <Ionicons
                    name={isDarkmode ? "sunny" : "moon"}
                    size={20}
                    color={isDarkmode ? themeColor.white100 : themeColor.dark}
                />
            }
            rightAction={() => {
                if (isDarkmode) {
                    setTheme("light");
                } else {
                    setTheme("dark");
                }
            }}
        />
      <View
        style={{
          flex: 1,
          alignItems: "center",
          justifyContent: "center",
        }}
      >
          { inputs.map((input,key) => (
              <View style={{
                  flexDirection: 'row-reverse',
                  justifyContent: 'start'
              }}>
                <TextInput placeholder={"Nom du joueur"} value={input.value} onChangeText={(text) => inputHandler(text, key)} />
                <TouchableOpacity onPress={() => deleteHandler(key)}>
                    <Ionicons
                        name={isDarkmode ? "person-remove-outline" : "person-remove"}
                        size={20}
                    />
                </TouchableOpacity>
              </View>
          ))}
          <Button
              style={{ marginTop: 10 }}
              text="Ajouter un nouveau joueur"
              status="info"
              onPress={addHandler}
          />

      </View>
    </Layout>
  );
}
